CREATE DATABASE COLLEGE;
USE COLLEGE;
CREATE TABLE STUDENT(NAME VARCHAR(20),STUDENT_NUMBER INT,CLASS INT,MAJOR VARCHAR(50));
CREATE TABLE COURSE(COURSE_NAME VARCHAR(100),COURSE_NUMBER VARCHAR(20) PRIMARY KEY,CREDIT_HOURS INT,DEPARTMENT VARCHAR(30));
CREATE TABLE PREREQUISITE(COURSE_NUMBER VARCHAR(20),PREREQUISITE_NUMBER VARCHAR(20) PRIMARY KEY,FOREIGN KEY(COURSE_NUMBER) REFERENCES COURSE(COURSE_NUMBER));
CREATE TABLE SECTION(SECTION_IDENTIFIER INT PRIMARY KEY,COURSE_NUMBER VARCHAR(20),SEMESTER VARCHAR(20),YEAR VARCHAR(2),INSTRUCTOR VARCHAR(30),FOREIGN KEY(COURSE_NUMBER) REFERENCES COURSE(COURSE_NUMBER));
CREATE TABLE GRADE_REPORT(STUDENT_NUMBER INT PRIMARY KEY,SECTION_IDENTIFIER INT,GRADE CHAR(1),FOREIGN KEY(SECTION_IDENTIFIER) REFERENCES SECTION(SECTION_IDENTIFIER));